if (GetConVar( "rpw_binoculars_hold" ) == nil) then	CreateConVar( "rpw_binoculars_hold", 0, FCVAR_ARCHIVE, "0 or 1, whether or not binoculars are toggle-to-use rather than hold-to-use." )endfunction rpw_PopulateOptionsMenu_Binoculars( CPanel )		CPanel:AddControl( "Checkbox", { Label = "Зажимать для прицеливания", Command = "rpw_binoculars_hold" } )endhook.Add( "PopulateToolMenu", "rpw_PopulateOptionsMenu_Binoculars", function()	spawnmenu.AddToolMenuOption( "Options", "Campo", "RPW_Binoculars", "Бинокль", "", "", rpw_PopulateOptionsMenu_Binoculars )end )